import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Dashboard = () => {
  const [stats, setStats] = useState({
    totalClientes: 0,
    totalTickets: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchStats();
  }, []);

  const fetchStats = async () => {
    try {
      const token = localStorage.getItem('token');
      const config = {
        headers: { Authorization: `Bearer ${token}` }
      };

      const response = await axios.get('/api/clientes', config);
      setStats({
        totalClientes: response.data.length,
        totalTickets: 0 // Esto se actualizar√≠a cuando implementes tickets
      });
    } catch (error) {
      console.error('Error obteniendo estad√≠sticas:', error);
    } finally {
      setLoading(false);
    }
  };

  const downloadReport = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('/api/reporte-excel', {
        headers: { Authorization: `Bearer ${token}` },
        responseType: 'blob'
      });

      const url = window.URL.createObjectURL(new Blob([response.data]));
      const link = document.createElement('a');
      link.href = url;
      link.setAttribute('download', 'reporte-clientes.xlsx');
      document.body.appendChild(link);
      link.click();
      link.remove();
    } catch (error) {
      console.error('Error descargando reporte:', error);
      alert('Error al descargar el reporte');
    }
  };

  if (loading) {
    return (
      <div className="text-center">
        <div className="spinner-border" role="status">
          <span className="visually-hidden">Cargando...</span>
        </div>
      </div>
    );
  }

  return (
    <div>
      <div className="row mb-4">
        <div className="col-12">
          <h1 className="mb-4">üìä Dashboard</h1>
        </div>
      </div>

      <div className="row mb-4">
        <div className="col-md-6 col-lg-3 mb-3">
          <div className="card text-center">
            <div className="card-body">
              <h5 className="card-title">üë• Total Clientes</h5>
              <h2 className="text-primary">{stats.totalClientes}</h2>
            </div>
          </div>
        </div>
        <div className="col-md-6 col-lg-3 mb-3">
          <div className="card text-center">
            <div className="card-body">
              <h5 className="card-title">üé´ Total Tickets</h5>
              <h2 className="text-success">{stats.totalTickets}</h2>
            </div>
          </div>
        </div>
      </div>

      <div className="row">
        <div className="col-md-6 mb-4">
          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">üöÄ Acciones R√°pidas</h5>
            </div>
            <div className="card-body">
              <div className="d-grid gap-2">
                <Link to="/nuevo-cliente" className="btn btn-primary">
                  ‚ûï Agregar Nuevo Cliente
                </Link>
                <Link to="/generar-ticket" className="btn btn-success">
                  üé´ Generar Ticket
                </Link>
                <Link to="/clientes" className="btn btn-info">
                  üë• Ver Todos los Clientes
                </Link>
              </div>
            </div>
          </div>
        </div>

        <div className="col-md-6 mb-4">
          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">üìä Reportes</h5>
            </div>
            <div className="card-body">
              <div className="d-grid gap-2">
                <button onClick={downloadReport} className="btn btn-outline-primary">
                  üì• Descargar Reporte Excel
                </button>
                <p className="text-muted small mt-2">
                  Descarga un reporte completo con todos los clientes y tickets generados
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="row">
        <div className="col-12">
          <div className="card">
            <div className="card-header">
              <h5 className="mb-0">‚ÑπÔ∏è Informaci√≥n del Sistema</h5>
            </div>
            <div className="card-body">
              <div className="row">
                <div className="col-md-6">
                  <h6>Funcionalidades Disponibles:</h6>
                  <ul>
                    <li>‚úÖ Gesti√≥n de clientes con datos completos</li>
                    <li>‚úÖ Generaci√≥n autom√°tica de IDs √∫nicos</li>
                    <li>‚úÖ Creaci√≥n de tickets personalizados</li>
                    <li>‚úÖ Descarga de tickets en formato PNG</li>
                    <li>‚úÖ Base de datos en Excel</li>
                    <li>‚úÖ Sistema de autenticaci√≥n seguro</li>
                  </ul>
                </div>
                <div className="col-md-6">
                  <h6>Datos del Cliente:</h6>
                  <ul>
                    <li>üìù Nombre Completo</li>
                    <li>üìû Tel√©fono</li>
                    <li>‚õ™ Iglesia</li>
                    <li>üÜî C√©dula de Identidad</li>
                    <li>üî¢ ID √önico (generado autom√°ticamente)</li>
                  </ul>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
